import "./MainSection.css";
import Cat from "./Cat.js";
import { useState , useEffect } from "react";
import {ethers} from "ethers";

function MainSection() {

  const [currentAccount, setCurrentAccount] = useState(null);
  const [chainId, setChainId] = useState(null);
  const [chainName, setChainName] = useState(null);
  const [balance, setBalance] = useState(null);
  const [blockNumber, setBlockNumber] = useState(null);

  const getData = async () => {
    console.log("HEY");
  }

  const getWalletAddress = async() => {
    if(window.ethereum && window.ethereum.isMetaMask){
      const provider = new ethers.providers.Web3Provider(window.ethereum);
      await provider.send("eth_requestAccounts");
      const currentAddress = await provider.getSigner().getAddress();
      console.log(currentAddress);
      setCurrentAccount(currentAddress)

      const chain = await provider.getNetwork();
      setChainId(chain.chainId);
      setChainName(chain.name);

      const amount = await provider.getBalance(currentAddress);
      const amountInEth = ethers.utils.formatEther(amount);
      setBalance(amountInEth);

      const blockNumber = await provider.getBlockNumber();
      setBlockNumber(blockNumber);
    }

  }

  const chainChanged = () => {
    window.location.reload();
  }
  window.ethereum.on('chainChanged', chainChanged);
  window.ethereum.on('accountsChanged', getWalletAddress);

  useEffect(() => {
    getWalletAddress();
  }, []);

  return (
    <div class="MainSection">
        <div class="Content">

            <p>{currentAccount}</p>
            <p>Chain Id: {chainId}</p>
            <p>Chain name: {chainName}</p>
            <p>Eth: {balance}</p>
            <p>Block#: {blockNumber}</p>
            <button onClick={getData}>Click me!</button>

        </div>

        <div class="SideBar">
            <Cat id="309" name ="Bob"/>
            <Cat id="310" name ="Alice"/>
            <Cat id="308" name ="Theodore"/>
            <Cat id="312"/>
        </div>

    </div>
  );
}

export default MainSection;
